mapper.insert:
		/* buyerInsertHist */

			INSERT INTO tb_buyer_hist (
				member_id,
				buyer_hist_idx,
				buyer_hist_nm,
				buyer_hist_invt,
				buyer_hist_spec,
				buyer_hist_dt,
				buyer_hist_content,
				process_cd,
				reg_dt,
				reg_id
			)
			VALUES(
				 <!-- (select concat('BI',IFNULL(LPAD(MAX(SUBSTRING(member_id,3,LENGTH(member_id))) + 1,4,'0'),'0000')) from tb_buyer_hist a) -->
				 (select member_id from tb_buyer order by reg_dt desc limit 1)
            	,(select concat('BH',IFNULL(LPAD(MAX(SUBSTRING(buyer_hist_idx,3,LENGTH(buyer_hist_idx))) + 1,4,'0'),'0000')) from tb_buyer_hist a)
				,#{buyerHistNmData}
				,#{buyerHistInvtData}
				,#{buyerHistSpecData}
				,#{buyerHistDtData}
				,#{buyerHistContentData}
				,#{buyerHistProData}
				,CURRENT_TIMESTAMP
				,'admin'<!-- 나중에 로그인 아이디 변경 -->
			)

mapper.select:
/*selectBuyerInformationManagementList*/
SELECT company_nm as companyNm ,member_id as memberId ,(select common_nm from ta_common where group_cd ='G00015'and order_nbr = buyer_bus_area_Cd) as buyerBusAreaCd ,buyer_ceo as buyerCeo ,if(buyer_head_tel = '' ,'' ,if(left(buyer_head_tel,2) = "02", concat(left(buyer_head_tel,2),"-",mid(buyer_head_tel,3,4),"-",right(buyer_head_tel,4)) , concat(left(buyer_head_tel,3),"-",mid(buyer_head_tel,4,4),"-",right(buyer_head_tel,4)) ) ) as buyerHeadTel ,if(buyer_head_addr !=null ,substr(buyer_head_addr,9,2) ,substr(buyer_branch_addr,9,2) ) as buyerHeadAddr FROM tb_buyer WHERE 1=1
AND company_nm LIKE CONCAT('%',#{searchText},'%') OR buyer_ceo LIKE CONCAT('%',#{searchText},'%')
AND company_nm LIKE CONCAT('%',#{searchText},'%')
AND buyer_ceo LIKE CONCAT('%',#{searchText},'%')
Order BY reg_dt asc LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

/*selectBusinessManagementList*/
SELECT a.* FROM (SELECT buss_anncemnt_no AS bussAnncemntNo ,buss_anncemnt_nm AS bussAnncemntNm ,(CASE WHEN DATE_FORMAT(now(),'%Y-%m-%d') BETWEEN buss_fr_dt AND buss_to_dt THEN '000002' WHEN DATE_FORMAT(now(),'%Y-%m-%d') > buss_to_dt THEN '000001' ELSE '000003' END ) AS anncemntStCd ,(SELECT common_nm FROM ta_common WHERE 1=1 AND use_flag = 'Y' AND group_cd = 'G00046' AND common_cd = anncemntStCd ) AS result ,reg_id AS regId ,Date_format(reg_dt, '%Y-%m-%d') AS regDt ,buss_fr_dt as bussFrDt ,buss_to_dt as bussToDt ,(SELECT count(*) FROM tb_buss_anncemnt_appl b WHERE 1=1 AND b.buss_anncemnt_no = bussAnncemntNo AND (b.applic_st_cd = '000001' or b.applic_st_cd = '000002') ) as applicStCd FROM tb_business_announcement) a WHERE 1=1
AND (a.bussAnncemntNo LIKE CONCAT('%',#{searchText},'%') OR a.bussAnncemntNm LIKE CONCAT('%',#{searchText},'%')) AND a.bussAnncemnNm LIKE CONCAT('%',#{searchText},'%') AND a.bussAnncemntNo LIKE CONCAT('%',#{searchText},'%')
AND (a.bussFrDt BETWEEN date_format(#{startDate}, '%Y-%m-%d') AND date_format(#{endDate}, '%Y-%m-%d') OR a.bussToDt BETWEEN date_format(#{startDate}, '%Y-%m-%d') AND date_format(#{endDate}, '%Y-%m-%d') )
AND a.anncemntStCd = '000001' AND a.anncemntStCd = '000002' AND a.anncemntStCd = '000003'
ORDER BY a.regDt DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
/* businessManagementRegisterUpeateSave */

UPDATE tb_business_announcement
SET
	buss_fr_dt = #{bussFrDt},
	buss_to_dt = #{bussToDt},
	buss_cost_amt = #{bussCostAmt},
	buss_dept_cd = #{bussDeptCd},
	buss_cont = #{bussCont}
WHERE buss_anncemnt_no = #{bussAnncemntNo}

			/* businessManagementRegisterUpdateButton */

			UPDATE tb_buss_anncemnt_attch_file a inner join tb_buss_anncemnt_appl b
			 on a.buss_anncemnt_no = b.buss_anncemnt_no
			 set b.applic_st_cd = '승인' 
			 where a.buss_anncemnt_no = b.buss_anncemnt_no 
 			    and a.buss_anncemnt_no = #{bussAnncemntNo}
       		    and a.attch_file_no = #{attchFileNo}

			/* businessManagementRegisterUpdateButton1 */

			UPDATE tb_buss_anncemnt_attch_file a inner join tb_buss_anncemnt_appl b
			 on a.buss_anncemnt_no = b.buss_anncemnt_no
			 set b.applic_st_cd = '반려' ,
			      b.remark = #{remark}
			 where a.buss_anncemnt_no = b.buss_anncemnt_no 
 			    and a.buss_anncemnt_no = #{bussAnncemntNo}
       		    and a.attch_file_no = #{attchFileNo}

mapper.delete:
