mapper.update:
		/*bussAnncemntApplUpdate*/

			UPDATE tb_buss_anncemnt_appl
   				 SET applic_st_cd = #{applicStCd}
                       ,remark = #{remark}
             WHERE 1=1
                 AND member_id = #{memberId}
                 AND buss_anncemnt_no = #{bussAnncemntNo}

mapper.delete:
		/*bussAnncemntApplUpdate*/

			DELETE tb_buss_anncemnt_appl
             WHERE 1=1
                 AND member_id = #{memberId}
                 AND buss_anncemnt_no = #{bussAnncemntNo}
